sp_widget.client_script:
function($scope,$rootScope) {
	/* widget controller */
	var c = this;

	$rootScope.$on('selectCase', function(event,data) {
		c.server.get({
			action: 'getCase',
			caseID: $rootScope.caseID
		}).then(function(r) {
			c.data.number = r.data.caserec.number;

			c.data.contact = r.data.caserec.contact;
			c.data.state = r.data.caserec.state;

		});
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */

	if (input && input.caseID) {
		var caserec = new GlideRecord('sn_customerservice_case');
		if (caserec.get(input.caseID)) {
			if (input.action == 'getCase') {
				data.caserec = {};
				data.caserec2 = {};
				$sp.getRecordDisplayValues(data.caserec, caserec, "number, contact, sys_id, state");
			}
		}
	}
})();
