sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
	c.selectItem = function(idx) {
		var id=c.data.cases[idx].sys_id;
		console.log('Case ID: ' + id);
		$rootScope.caseID = id;
		//Send an event
		$rootScope.$emit('selectCase', id);

	}
}

sp_widget.script:
(function() {
	//create an array to populate without cases
	data.cases = [];
	var CaseGR = new GlideRecord('sn_customerservice_case');
	CaseGR.addQuery('assigned_to', gs.getUser().getID());
	CaseGR.orderByDesc('sys_created_on');
	CaseGR.query();
	while (CaseGR.next()) {
		var caseObj = {};
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(caseObj, CaseGR, 'number,short_description,sys_id,contact,account,state');
		//get the first 20 characters of the description
		caseObj.short_description = CaseGR.getValue('short_description').slice(0,100);
		//push the populated obj into the array
		data.cases.push(caseObj);
	}
})();
